// what this module does
	// input =>	file path/url and specify if it is local or url
	// output => html of pages
	// process => getting html from files

var fs = require('fs');
var request = require('request');

var get = function(arg,path,callback){
	htmlarr = [];
	if(arg == "local"){
		// one for local files
		function getHtml(){
			fs.readdir(path, function(err, files){
				files.filter(function(file){ 
	         		return file.substr(-5) === '.html'; 
	         	}).forEach(function(file){
	         		fs.readFile(path+'/'+file, 'utf-8', function(err,html){
	         			htmlarr.push(html);
	       			}); 
	       		});
			});
		}

		function showdata(){
			getHtml();
			setTimeout(function(){
				callback(htmlarr);
			},100);
		}

		showdata();

	}else if(arg=="url"){
		// one for url's
		request(path, function(error,response,html){
			if(!error){
				callback(html);
			}
		});
	}
}

module.exports = {
	get: get
}